import React from 'react';
import { ResponsivePie } from '@nivo/pie';
import theme from '../../Theme/theme';
import bp from '../Theme/breakpoints';
import styled, { css } from 'styled-components';

const ResponsiveWrapper = styled.h1`
	max-width: 700px;
	color: ${theme.color.text.primary};
	margin-bottom: 16px;
	display: flex;
	justify-content: center;
	position: relative;
	text-align: flex-start;
	font-size: 36px;
	@media ${bp.sm} {
		text-align: left;
		font-size: 50px;
	}
`;

const pieData = [
	{
		id: 'java',
		label: 'java',
		value: 195,
		color: 'hsl(90, 70%, 50%)',
	},
	{
		id: 'erlang',
		label: 'erlang',
		value: 419,
		color: 'hsl(56, 70%, 50%)',
	},
	{
		id: 'ruby',
		label: 'ruby',
		value: 407,
		color: 'hsl(103, 70%, 50%)',
	},
	{
		id: 'haskell',
		label: 'haskell',
		value: 474,
		color: 'hsl(186, 70%, 50%)',
	},
	{
		id: 'go',
		label: 'go',
		value: 71,
		color: 'hsl(104, 70%, 50%)',
	},
];

const Pie = () => {
	return (
		<ResponsiveWrapper>
			<ResponsivePie
				data={pieData}
				margin={{ top: 40, right: 80, bottom: 80, left: 80 }}
				innerRadius={0.5}
				padAngle={0.7}
				cornerRadius={3}
				activeOuterRadiusOffset={8}
				borderWidth={1}
				borderColor={{ from: 'color', modifiers: [['darker', 0.2]] }}
				arcLinkLabelsSkipAngle={10}
				arcLinkLabelsTextColor="#333333"
				arcLinkLabelsThickness={2}
				arcLinkLabelsColor={{ from: 'color' }}
				arcLabelsSkipAngle={10}
				arcLabelsTextColor={{ from: 'color', modifiers: [['darker', 2]] }}
			/>
		</ResponsiveWrapper>
	);
};

export default Pie;
