import styled from 'styled-components';
import theme from '../../Theme/theme';
import ScrollDownCta from '../../ScrollDownCTA/ScrollDownCTA';
import ETF_big from '../../../assets/images/etf_big.png';
import vault from '../../../assets/images/vault_render_big.png';
import graph from '../../../assets/images/vsl_graph_both.png';
import lock from '../../../assets/images/lock_cube.png';
import scales from '../../../assets/images/scales_zoomed_cropped_4.png';
import InformationButton from '../../Button/InformationButton/InformationButton';
import InformationButtonAccent from '../../Button/InformationButtonAccent/InformationButtonAccent';
import PrimaryButton from '../../Button/Primary/PrimaryButton';
import bp from '../../Theme/breakpoints';
import Footer from '../../Navigation/Footer/Footer';
import { useAuth0 } from '@auth0/auth0-react';
import blueGlow from '../../../assets/images/blue_glo.svg';
import greenGlow from '../../../assets/images/green_glo.svg';
import pinkGlow from '../../../assets/images/pink_glo.svg';
import darkBlueGlow from '../../../assets/images/darkblue_glo.svg';

const PageWrapper = styled.div`
	padding: 0 48px 64px 48px;
	max-width: 1560px;
	margin: 0 auto;
`;

const AboutSection = styled.section`
	display: flex;
	justify-content: space-between;
	background-color: ${theme.color.background};
	border-radius: 50px;
`;

const ButtonContainer = styled.div`
	display: flex;
	flex-direction: row;
	justify-content: space-evenly;
	align-items: center;
	padding-top: 20px;
`;

const SectionWrapper = styled.div`
	border-radius: 15px;
	padding: 48px 24px;
	width: 100%;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	overflow: hidden;
	z-index: 2;
`;

const BackgroundBlurLeft = styled.img`
	left: -20%;
	position: absolute;
	max-height: 900px;
	min-height: 900px;
	min-width: 600px;
	margin-top: -300px;
`;

const BackgroundBlurRight = styled.img`
	right: -20%;
	position: absolute;
	max-height: 900px;
	min-height: 900px;
	min-wifth: 600px;
	margin-top: -300px;
`;

const AboutSectionHeader = styled.h1`
	width: 80%;
	color: ${theme.color.text};
	margin-bottom: 16px;
	text-align: center;
	font-size: 64px;
	@media ${bp.sm} {
		text-align: left;
	}
`;

const AboutImageParent = styled.div`
	width: 100%;
	align-items: center;
	justify-content: center;
	display: flex;
	margin-bottom: 24px;
	@media ${bp.sm} {
		max-width: 400px;
		align-items: center;
		display: flex;
	}
`;

const AboutImg = styled.img`
	max-width: 50%;
	margin-bottom: 24px;
	@media ${bp.sm} {
		max-width: 70%;
	}
`;

const AboutPara = styled.p`
	color: ${theme.color.text};
	max-width: 500px;
	text-align: center;
	font-size: 22px;
	@media ${bp.sm} {
		text-align: left;
	}
`;

const AboutWrapperTextLeft = styled.div`
	z-index: 2;
	@media ${bp.sm} {
		width: 90%;
		display: flex;
		flex-direction: row-reverse;
		justify-content: space-between;
	}
`;

const AboutWrapperTextRight = styled.div`
	z-index: 2;
	@media ${bp.sm} {
		width: 90%;
		display: flex;
		justify-content: space-between;
		z-index: 2;
	}
`;

const AboutTextWrapperContainer = styled.div`
	max-width: 500px;
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	@media ${bp.sm} {
		align-items: flex-start;
	}
`;

const HeroSectionHeader = styled.h1`
	color: ${theme.color.text};
	margin-bottom: 16px;
	text-align: center;
	font-size: 64px;
	@media ${bp.sm} {
		text-align: left;
	}
`;

const HeroImg = styled.img`
	max-width: 50%;
	margin-bottom: 24px;
	@media ${bp.sm} {
		max-width: 70%;
	}
`;

const HeroPara = styled.p`
	color: ${theme.color.text};
	max-width: 400px;
	text-align: center;
	font-size: 22px;
	@media ${bp.sm} {
		text-align: left;
	}
`;

const HeroWrapper = styled.div`
	z-index: 2;
	@media ${bp.sm} {
		width: 90%;
		display: flex;
		flex-direction: row-reverse;
		justify-content: space-between;
	}
`;

const HeroTextWrapper = styled.div`
	max-width: 500px;
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	@media ${bp.sm} {
		align-items: flex-start;
	}
`;

const LandingPage = () => {
	const { isAuthenticated } = useAuth0();
	return (
		<>
			<PageWrapper>
				<AboutSection>
					<SectionWrapper>
