import styled from 'styled-components';
import theme from '../../Theme/theme';
import ScrollDownCta from '../../ScrollDownCTA/ScrollDownCTA';
import ETF_big from '../../../assets/images/etf_big.png';
import vault from '../../../assets/images/vault_render_big.png';
import graph from '../../../assets/images/vsl_graph_both.png';
import lock from '../../../assets/images/lock_cube.png';
import scales from '../../../assets/images/scales_zoomed_cropped_4.png';
import InformationButton from '../../Button/InformationButton/InformationButton';
import InformationButtonAccent from '../../Button/InformationButtonAccent/InformationButtonAccent';
import PrimaryButton from '../../Button/Primary/PrimaryButton';
import bp from '../../Theme/breakpoints';
import Footer from '../../Navigation/Footer/Footer';
import { useAuth0 } from '@auth0/auth0-react';
import blueGlow from '../../../assets/images/blue_glo.svg';
import greenGlow from '../../../assets/images/green_glo.svg';
import pinkGlow from '../../../assets/images/pink_glo.svg';
import darkBlueGlow from '../../../assets/images/darkblue_glo.svg';

const PageWrapper = styled.div`
	padding: 0 48px 64px 48px;
	max-width: 1560px;
	margin: 0 auto;
`;

const AboutSection = styled.section`
	display: flex;
	justify-content: center;
`;

const SectionWrapper = styled.div`
	border-radius: 15px;
	padding: 48px 24px;
	width: 100%;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
`;

const BlueBackgroundBlur = styled.div`
	background-image: url(${blueGlow});
	background-size: 100%;
	z-index: -1;
`;

const GreenBackgroundBlur = styled.div`
	background-image: url(${greenGlow});
`;

const PinkBackgroundBlur = styled.div`
	background-image: url(${pinkGlow});
`;

const DarkBlueBackgroundBlur = styled.div`
	background-image: url(${darkBlueGlow});
`;

const AboutSectionHeader = styled.h1`
	color: ${theme.color.text};
	margin-bottom: 16px;
	text-align: center;

	@media ${bp.sm} {
		text-align: left;
	}
`;

const AboutImg = styled.img`
	width: 100%;
	margin-bottom: 24px;

	@media ${bp.sm} {
		max-width: 400px;
	}
`;

const AboutPara = styled.p`
	color: ${theme.color.text};
	max-width: 400px;
	text-align: center;
	@media ${bp.sm} {
		text-align: left;
	}
`;

const AboutWrapper = styled.div`
	@media ${bp.sm} {
		width: 90%;
		display: flex;
		justify-content: space-around;
	}
`;

const AboutTextWrapper = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	@media ${bp.sm} {
		align-items: flex-start;
	}
`;

const HeroSectionHeader = styled.h1`
	color: ${theme.color.text};
	margin-bottom: 16px;
	text-align: center;

	@media ${bp.sm} {
		text-align: left;
	}
`;

const HeroImg = styled.img`
	width: 100%;
	margin-bottom: 24px;

	@media ${bp.sm} {
		max-width: 400px;
	}
`;

const HeroPara = styled.p`
	color: ${theme.color.text};
	max-width: 400px;
	text-align: center;
	@media ${bp.sm} {
		text-align: left;
	}
`;

const HeroWrapper = styled.div`
	@media ${bp.sm} {
		width: 90%;
		display: flex;
		justify-content: space-around;
	}
`;

const HeroTextWrapper = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	@media ${bp.sm} {
		align-items: flex-start;
	}
`;

const LandingPage = () => {
	const { isAuthenticated } = useAuth0();
	return (
		<>
			<PageWrapper>
				<AboutSection>
					<SectionWrapper>
						<HeroWrapper>
							<HeroTextWrapper>
								<HeroSectionHeader>Welcome Aboard.</HeroSectionHeader>
								<HeroPara>the first synthetic deflationary DEFI mutual fund</HeroPara>
							</HeroTextWrapper>
							<HeroImg src={ETF_big} alt="About" />
						</HeroWrapper>
						<InformationButton>About</InformationButton>
						<InformationButtonAccent>Connect Wallet</InformationButtonAccent>
					</SectionWrapper>
				</AboutSection>
				<AboutSection>
					<SectionWrapper>
						<AboutWrapper>
							<AboutTextWrapper>
								<AboutSectionHeader>Synthetic Mutual Fund.</AboutSectionHeader>
								<AboutPara>
									Our token is backed by a carefully selected collection of blue-chip
									cryptocurrencies.
								</AboutPara>
							</AboutTextWrapper>
							<GreenBackgroundBlur>
								<AboutImg src={vault} alt="About" />
							</GreenBackgroundBlur>
						</AboutWrapper>
					</SectionWrapper>
				</AboutSection>
				<AboutSection>
					<SectionWrapper>
						<AboutWrapper>
							<BlueBackgroundBlur>
								<AboutImg src={graph} alt="About" />
							</BlueBackgroundBlur>
							<AboutTextWrapper>
								<AboutSectionHeader>Do better than the market...</AboutSectionHeader>
								<AboutPara>
									Our protocol exposes you to a variety of the top-tier tokens that are set to do
									better than most.
								</AboutPara>
							</AboutTextWrapper>
						</AboutWrapper>
					</SectionWrapper>
				</AboutSection>
				<AboutSection>
					<SectionWrapper>
						<AboutWrapper>
							<AboutTextWrapper>
								<AboutSectionHeader>Maximal safety, Minimal risk.</AboutSectionHeader>
								<AboutPara>
									Built-in diversification, decentralized governance, and deflationary mechanisms keep
									your assets value secure.
								</AboutPara>
							</AboutTextWrapper>
							<DarkBlueBackgroundBlur>
								<AboutImg src={lock} alt="About" />
							</DarkBlueBackgroundBlur>
						</AboutWrapper>
					</SectionWrapper>
				</AboutSection>
				<AboutSection>
					<SectionWrapper>
						<AboutWrapper>
							<PinkBackgroundBlur>
								<AboutImg src={scales} alt="About" />
							</PinkBackgroundBlur>
							<AboutTextWrapper>
								<AboutSectionHeader>Reserve Vault & Burns</AboutSectionHeader>
								<AboutPara>
									We utilise a reserve vault of tokens that can be altered in supply to affect the
									$VSL price to match the mutual fund.
								</AboutPara>
							</AboutTextWrapper>
						</AboutWrapper>
					</SectionWrapper>
				</AboutSection>
			</PageWrapper>
			<Footer />
		</>
	);
};

export default LandingPage;
