import styled from 'styled-components';
import theme from '../../Theme/theme';
import ScrollDownCta from '../../ScrollDownCTA/ScrollDownCTA';
import ETF_big from '../../../assets/images/etf_big.png';
import vault from '../../../assets/images/vault_render_big.png';
import graph from '../../../assets/images/etf_big.png';
import lock from '../../../assets/images/lock_cube.png';
import scales from '../../../assets/images/scales_zoomed_cropped_4.png';
import PrimaryButton from '../../Button/Primary/PrimaryButton';
import About from '../../../assets/svgs/about.svg';
import Create from '../../../assets/svgs/create.svg';
import Share from '../../../assets/svgs/share.svg';
import Survey from '../../../assets/svgs/survey.svg';
import ViewResults from '../../../assets/svgs/view-results.svg';
import bp from '../../Theme/breakpoints';
import Footer from '../../Navigation/Footer/Footer';
import SignupButton from '../../Auth/SignupButton/SignupButton';
import { Link } from 'react-router-dom';
import { useAuth0 } from '@auth0/auth0-react';

const PageWrapper = styled.div`
	padding: 0 48px 64px 48px;
	max-width: 1560px;
	margin: 0 auto;
`;

const AboutSection = styled.section`
	display: flex;
	justify-content: center;
`;

const GlassmorphicWrapper = styled.div`
	background: rgb(0, 0, 0);
	background: linear-gradient(90deg, rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, 0) 0%);
	backdrop-filter: blur(4px);
	-webkit-backdrop-filter: blur(4px);
	border-radius: 15px;
	padding: 48px 24px;
	width: 100%;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
`;

const AboutSectionHeader = styled.h1`
	color: ${theme.color.text};
	margin-bottom: 16px;
	text-align: center;

	@media ${bp.sm} {
		text-align: left;
	}
`;

const AboutImg = styled.img`
	width: 100%;
	margin-bottom: 24px;

	@media ${bp.sm} {
		max-width: 400px;
	}
`;

const AboutPara = styled.p`
	color: ${theme.color.text};
	max-width: 400px;
	text-align: center;
	@media ${bp.sm} {
		text-align: left;
	}
`;

const AboutWrapper = styled.div`
	@media ${bp.sm} {
		width: 90%;
		display: flex;
		justify-content: space-around;
	}
`;

const AboutTextWrapper = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	@media ${bp.sm} {
		align-items: flex-start;
	}
`;

const LandingPage = () => {
	const { isAuthenticated } = useAuth0();
	return (
		<>
			<PageWrapper>
				<AboutSection>
					<GlassmorphicWrapper>
						<AboutWrapper>
							<AboutTextWrapper>
								<AboutSectionHeader>Welcome Aboard.</AboutSectionHeader>
								<AboutPara>the first synthetic deflationary mutual fund</AboutPara>
							</AboutTextWrapper>
							<AboutImg src={ETF_big} alt="About" />
						</AboutWrapper>
					</GlassmorphicWrapper>
				</AboutSection>
				<AboutSection>
					<GlassmorphicWrapper>
						<AboutWrapper>
							<AboutTextWrapper>
								<AboutSectionHeader>Synthetic Mutual Fund.</AboutSectionHeader>
								<AboutPara>
									Our token is backed by a carefully selected collection of blue-chip
									cryptocurrencies.
								</AboutPara>
							</AboutTextWrapper>
							<AboutImg src={vault} alt="About" />
						</AboutWrapper>
					</GlassmorphicWrapper>
				</AboutSection>
				<AboutSection>
					<GlassmorphicWrapper>
						<AboutWrapper>
							<AboutImg src={graph} alt="About" />
							<AboutTextWrapper>
								<AboutSectionHeader>Do better than the market...</AboutSectionHeader>
								<AboutPara>
									Our protocol exposes you to a variety of the top-tier tokens that are set to do
									better than most.
								</AboutPara>
							</AboutTextWrapper>
						</AboutWrapper>
					</GlassmorphicWrapper>
				</AboutSection>
				<AboutSection>
					<GlassmorphicWrapper>
						<AboutWrapper>
							<AboutTextWrapper>
								<AboutSectionHeader>Maximal safety, Minimal risk.</AboutSectionHeader>
								<AboutPara>
									Built-in diversification, decentralized governance, and deflationary mechanisms keep
									your assets value secure.
								</AboutPara>
							</AboutTextWrapper>
							<AboutImg src={lock} alt="About" />
						</AboutWrapper>
					</GlassmorphicWrapper>
				</AboutSection>
				<AboutSection>
					<GlassmorphicWrapper>
						<AboutWrapper>
							<AboutImg src={scales} alt="About" />
							<AboutTextWrapper>
								<AboutSectionHeader>Reserve Vault & Burns</AboutSectionHeader>
								<AboutPara>
									We utilise a reserve vault of tokens that can be altered in supply to affect the
									$VSL price to match the mutual fund.
								</AboutPara>
							</AboutTextWrapper>
						</AboutWrapper>
					</GlassmorphicWrapper>
				</AboutSection>
			</PageWrapper>
			<Footer />
		</>
	);
};

export default LandingPage;
