import styled from 'styled-components';
import theme from '../../Theme/theme';
import ScrollDownCta from '../../ScrollDownCTA/ScrollDownCTA';
import CardStack from '../../../assets/images/etf_big.png';
import PrimaryButton from '../../Button/Primary/PrimaryButton';
import About from '../../../assets/svgs/about.svg';
import Create from '../../../assets/svgs/create.svg';
import Share from '../../../assets/svgs/share.svg';
import Survey from '../../../assets/svgs/survey.svg';
import ViewResults from '../../../assets/svgs/view-results.svg';
import bp from '../../Theme/breakpoints';
import Footer from '../../Navigation/Footer/Footer';
import SignupButton from '../../Auth/SignupButton/SignupButton';
import { Link } from 'react-router-dom';
import { useAuth0 } from '@auth0/auth0-react';

const PageWrapper = styled.div`
	padding: 0 48px 64px 48px;
	max-width: 1560px;
	margin: 0 auto;
`;

const HeroSection = styled.section`
	padding: 48px 0 60px 0;
	display: flex;
	flex-direction: column;
	align-items: center;
	text-align: center;
	margin: 0 auto;

	@media ${bp.sm} {
		display: flex;
		flex-direction: row;
		justify-content: center;
		align-items: center;
		max-width: 600px;

		padding-top: 0px;
		text-align: left;
	}
`;

const HeroHeader = styled.h1`
	color: ${theme.color.accent};
`;

const HeroSubHeader = styled.h2`
	margin-top: 24px;
	color: ${theme.color.text};
`;

const HeroImg = styled.img`
	margin-top: 24px;
	width: 80%;
	max-width: 556px;
	@media ${bp.sm} {
		margin-left: 24px;
	}
`;

const ScrollCTADiv = styled.div`
	display: none;
	@media ${bp.sm} {
		margin-top: 64px;
		display: flex;
		align-items: center;
	}
`;

const ScrollCTAPara = styled.p`
	margin-left: 16px;
`;

const AboutSection = styled.section`
	display: flex;
	justify-content: center;
`;

const GlassmorphicWrapper = styled.div`
	background: rgb(10, 10, 10);
	background: linear-gradient(90deg, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 0.2) 100%);
	backdrop-filter: blur(4px);
	-webkit-backdrop-filter: blur(4px);
	border-radius: 15px;
	padding: 48px 24px;
	width: 100%;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
`;

const AboutSectionHeader = styled.h3`
	color: ${theme.color.text};
	margin-bottom: 16px;
	text-align: center;

	@media ${bp.sm} {
		text-align: left;
	}
`;

const CTASectionHeader = styled.h3`
	color: ${theme.color.accent};
	margin-bottom: 16px;
	text-align: center;
`;

const AboutImg = styled.img`
	width: 100%;
	margin-bottom: 24px;

	@media ${bp.sm} {
		max-width: 400px;
	}
`;

const AboutPara = styled.p`
	color: ${theme.color.text};
	max-width: 400px;
	text-align: center;
	@media ${bp.sm} {
		text-align: left;
	}
`;

const AboutWrapper = styled.div`
	@media ${bp.sm} {
		width: 90%;
		display: flex;
		justify-content: space-around;
	}
`;

const AboutTextWrapper = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	@media ${bp.sm} {
		align-items: flex-start;
	}
`;

const CTAButtonDashboard = styled(Link)`
	text-decoration: none;
`;

const LandingPage = () => {
	const { isAuthenticated } = useAuth0();
	return (
		<>
			<PageWrapper>
				<AboutSection>
					<GlassmorphicWrapper>
						<AboutWrapper>
							<AboutTextWrapper>
								<AboutSectionHeader>Welcome Aboard.</AboutSectionHeader>
								<AboutPara>the first synthetic deflationary mutual fund</AboutPara>
							</AboutTextWrapper>
							<AboutImg src={CardStack} alt="About" />
						</AboutWrapper>
					</GlassmorphicWrapper>
				</AboutSection>
				<AboutSection>
					<GlassmorphicWrapper>
						<AboutWrapper>
							<AboutImg src={CardStack} alt="About" />
							<AboutTextWrapper>
								<AboutSectionHeader>Welcome Aboard.</AboutSectionHeader>
								<AboutPara>the first synthetic deflationary mutual fund</AboutPara>
							</AboutTextWrapper>
						</AboutWrapper>
					</GlassmorphicWrapper>
				</AboutSection>
				<AboutSection>
					<GlassmorphicWrapper>
						<AboutWrapper>
							<AboutTextWrapper>
								<AboutSectionHeader>Welcome Aboard.</AboutSectionHeader>
								<AboutPara>the first synthetic deflationary mutual fund</AboutPara>
							</AboutTextWrapper>
							<AboutImg src={CardStack} alt="About" />
						</AboutWrapper>
					</GlassmorphicWrapper>
				</AboutSection>
				<AboutSection>
					<GlassmorphicWrapper>
						<AboutWrapper>
							<AboutImg src={CardStack} alt="About" />
							<AboutTextWrapper>
								<AboutSectionHeader>Welcome Aboard.</AboutSectionHeader>
								<AboutPara>the first synthetic deflationary mutual fund</AboutPara>
							</AboutTextWrapper>
						</AboutWrapper>
					</GlassmorphicWrapper>
				</AboutSection>

				<GlassmorphicWrapper>
					<CTASectionHeader>Have a decision to make?</CTASectionHeader>
					{isAuthenticated ? (
						<PrimaryButton>
							<CTAButtonDashboard to="/dashboard">Go to dashboard</CTAButtonDashboard>
						</PrimaryButton>
					) : (
						<SignupButton />
					)}
				</GlassmorphicWrapper>
			</PageWrapper>
			<Footer />
		</>
	);
};

export default LandingPage;
