import styled from 'styled-components';
import theme from '../../Theme/theme';
import ETF_big from '../../../assets/images/etf_big.png';
import vault from '../../../assets/images/vault_render_big.png';
import graph from '../../../assets/images/vsl_graph_both.png';
import lock from '../../../assets/images/lock_cube.png';
import scales from '../../../assets/images/scales_zoomed_cropped_4.png';
import InformationButton from '../../Button/InformationButton/InformationButton';
import InformationButtonAccent from '../../Button/InformationButtonAccent/InformationButtonAccent';
import bp from '../../Theme/breakpoints';
import Footer from '../../Navigation/Footer/Footer';
import blueGlow from '../../../assets/images/blue_glo.svg';
import greenGlow from '../../../assets/images/green_glo.svg';
import pinkGlow from '../../../assets/images/pink_glo.svg';
import darkBlueGlow from '../../../assets/images/darkblue_glo.svg';
import SSTarrow from '../../../assets/images/uiButtons/double-arrow-up.svg';
import 'animate.css/animate.min.css';
import { AnimationOnScroll } from 'react-animation-on-scroll';
import ScrollToTop from 'react-scroll-to-top';
import { useEffect } from 'react';

const PageWrapper = styled.div`
	padding: 0 28px 64px 28px;
	max-width: 1560px;
	margin: 0 auto;
	height: 100%;
	background-color: transparent;
	position: relative;
	overflow: hidden;
	@media ${bp.sm} {
		position: static;
	}
`;

const AboutSection = styled.section`
	display: flex;
	justify-content: space-between;
	border-radius: 50px;
	position: relative;
	z-index: 0;
	background-color: transparent;
`;

const ButtonContainer = styled.div`
	display: flex;
	flex-direction: row;
	justify-content: space-evenly;
	align-items: center;
	padding-top: 20px;
	position: relative;
	z-index: 999;
`;

const SectionWrapper = styled.div`
	border-radius: 15px;
	padding: 48px 24px;
	width: 100%;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	position: relative;
	background-color: transparent;
`;

const AboutSectionHeader = styled.h1`
	z-index: 1;
	max-width: 700px;
	color: ${theme.color.text.primary};
	margin-bottom: 16px;
	text-align: flex-start;
	font-size: 36px;
	@media ${bp.sm} {
		text-align: left;
	}
`;

const AboutImageParent = styled.div`
	width: 100%;
	align-items: center;
	justify-content: center;
	display: flex;
	margin-bottom: 24px;
	@media ${bp.sm} {
		max-width: 400px;
		align-items: center;
		display: flex;
	}
`;

const AboutImg = styled.img`
	max-width: 60%;
	margin-bottom: 24px;
	@media ${bp.sm} {
		max-width: 70%;
	}
`;

const AboutPara = styled.p`
	color: ${theme.color.text.primary};
	max-width: 700px;
	text-align: flex-start;
	font-size: 18px;
	@media ${bp.sm} {
		text-align: left;
	}
`;

const AboutWrapperTextLeft = styled.div`
	padding-top: 50px;
	padding-bottom: 50px;
	position: relative;
	z-index: 2;
	@media ${bp.sm} {
		width: 90%;
		display: flex;
		flex-direction: row-reverse;
		justify-content: space-between;
		z-index: 2;
	}
`;

const AboutWrapperTextRight = styled.div`
	padding-top: 50px;
	padding-bottom: 50px;
	position: relative;
	z-index: 2;
	@media ${bp.sm} {
		width: 90%;
		display: flex;
		justify-content: space-between;
		z-index: 2;
	}
`;

const AboutTextWrapperContainer = styled.div`
	z-index: 2;
	max-width: 500px;
	display: flex;
	flex-direction: column;
	align-items: flex-start;
	justify-content: center;
	@media ${bp.sm} {
		align-items: flex-start;
	}
`;

const HeroSectionHeader = styled.h1`
	color: ${theme.color.text.primary};
	margin-bottom: 16px;
	text-align: flex-start;
	font-size: 32px;
	@media ${bp.sm} {
		text-align: left;
	}
`;

const HeroImg = styled.img`
	max-width: 70%;
	margin-bottom: 24px;
	@media ${bp.sm} {
		max-width: 90%;
	}
`;

const HeroPara = styled.p`
	color: ${theme.color.text.primary};
	max-width: 400px;
	text-align: flex-start;
	font-size: 18px;
	@media ${bp.sm} {
		text-align: left;
	}
`;

const HeroWrapper = styled.div`
	position: relative;
	z-index: 2;
	background-color: transparent;
	max-width: 900px;
	@media ${bp.sm} {
		padding-top: 50px;
		padding-bottom: 100px;
		width: 100%;
		display: flex;
		flex-direction: row;
		justify-content: space-between;
		z-index: 2;
		position: relative;
	}
`;

const HeroTextWrapper = styled.div`
	z-index: 2;
	max-width: 900px;
	display: flex;
	flex-direction: column;
	align-items: flex-start;
	justify-content: center;
	margin-right: 50px;
	@media ${bp.sm} {
		z-index: 2;
		align-items: flex-start;
	}
`;

const SSTimg = styled.img`
	width: 30px;
`;

const BlurWrapper = styled.div`
	max-height: 1050px;
	opacity: 50%;
`;

const BackgroundBlurLeft = styled.img`
	left: -30%;
	position: absolute;
	overflow: hidden;
	max-height: 925px;
	min-height: 900px;
	min-width: 600px;
	margin-top: -200px;
	object-fit: cover;
	z-index: -1;
	@media ${bp.sm} {
		left: -5%;
		object-fit: contain;
		max-height: 1250px;
		min-height: 900px;
		max-width: 100%;
		margin-top: -400px;
		z-index: -1;
	}

	@media ${bp.xl} {
		left: -5%;
		object-fit: cover;
		max-height: 1450px;
		min-height: 900px;
		width: 2000px;
		margin-top: -700px;
		z-index: -1;
	}
`;

const BackgroundBlurRight = styled.img`
	position: absolute;
	right: 0;
	overflow: hidden;
	max-height: 950px;
	min-height: 900px;
	min-width: 600px;
	margin-top: -200px;
	object-fit: cover;
	z-index: -1;
	@media ${bp.sm} {
		object-fit: contain;
		right: 0;
		max-height: 1250px;
		min-height: 900px;
		max-width: 100%;
		margin-top: -400px;
		z-index: -1;
	}

	@media ${bp.xl} {
		object-fit: contain;
		right: 0;
		object-fit: cover;
		max-height: 1450px;
		min-height: 900px;
		width: 2000px;
		margin-top: -700px;
		z-index: -1;
	}
`;

const AboutPage = () => {
	useEffect(() => {
		window.scrollTo({ top: 0, behavior: 'instant' });
	}, []);

	return (
		<>
			<ScrollToTop
				smooth
				width="480"
				height="480"
				color="#ffffff"
				style={{
					'background-color': 'transparent',
					'box-shadow': 'none',
					transition: 'opacity 1s ease-in-out',
					'&:active': {
						visibility: 'visible',
						transition: 'opacity 1s ease-in-out',
						opacity: '1',
					},
				}}
				component={<SSTimg src={SSTarrow} alt="sst" />}
			/>
			<PageWrapper>
				<AnimationOnScroll animateIn="animate__fadeIn" animateOnce="true">
					<AboutSection>
						<SectionWrapper>
							<HeroWrapper>
								<HeroTextWrapper>
									<HeroSectionHeader>Vessel Finance.</HeroSectionHeader>
									<HeroPara>
										Vessel Protocol aims to bring one of the most proven utilities on traditional
										markets, mutual funds, onto the decentralized web. \nWe present a novel approach
										to how we have tackled this challenge in this paper - a DeFi utility token akin
										to an ETF whose value is coupled to the assets which are encapsulated within the
										decentralized mutual fund our protocol provides. The fund itself is a trustless
										DAO, whereby user votes from one epoch to the next shape how constituent assets
										within it evolve using a voting algorithm we elaborate upon in this paper. Most
										ETF tokens work by ”wrapping” a set of reserve assets, however, our protocol
										emulates the price action of the assets with the help of token reserves and
										token burns, thereby rendering the ”reserve assets” in our prior comparison
										synthetic. In order to incentivize decentralization, there is an established
										bounty scheme within the protocol to ensure fairness and to encourage timely
										protocol re-stablization. A guaranteed token burn establishes a locked
										deflationary mechanic, and over time the token supply is guaranteed to diminish
										with volume. Thus, overall, the aforementioned combines the established
										advantages of diversification in mutual funds with the benefits of
										decentralization and deflationary mechanics and removes the element of ”opaque”
										management over how ETFs are shaped or how they evolve over time.
									</HeroPara>
								</HeroTextWrapper>
							</HeroWrapper>
						</SectionWrapper>
					</AboutSection>
				</AnimationOnScroll>

