import React from 'react';
import { ResponsivePie } from '@nivo/pie';
import theme from '../Theme/theme';
import bp from '../Theme/breakpoints';
import styled, { css } from 'styled-components';

const pieData = [
	{
		id: 'Seed Sale',
		label: 'Seed Sale',
		value: 3,
		color: 'hsl(90, 70%, 50%)',
	},
	{
		id: 'Private Sale',
		label: 'Private Sale',
		value: 5,
		color: 'hsl(56, 70%, 50%)',
	},
	{
		id: 'Public sale',
		label: 'Public sale',
		value: 7,
		color: 'hsl(103, 70%, 50%)',
	},
	{
		id: 'Team and Advisors',
		label: 'Team and Advisors',
		value: 6.6,
		color: 'hsl(186, 70%, 50%)',
	},
	{
		id: 'Marketing',
		label: 'Marketing',
		value: 1.6,
		color: 'hsl(104, 70%, 50%)',
	},
	{
		id: 'Development',
		label: 'Development',
		value: 3.3,
		color: 'hsl(90, 70%, 50%)',
	},
	{
		id: 'Bounty',
		label: 'Bounty',
		value: 2.5,
		color: 'hsl(56, 70%, 50%)',
	},
	{
		id: 'Liquidity Reserves',
		label: 'Liquidity Reserves',
		value: 5,
		color: 'hsl(103, 70%, 50%)',
	},
	{
		id: 'Protocol Vault',
		label: 'Protocol Vault',
		value: 33,
		color: 'hsl(186, 70%, 50%)',
	},
	{
		id: 'Protocol Burn',
		label: 'Protocol Burn',
		value: 33,
		color: 'hsl(104, 70%, 50%)',
	},
];

const Pie = () => {
	return (
		<ResponsivePie
			data={pieData}
			margin={{ top: 30, right: 40, bottom: 30, left: 40 }}
			innerRadius={0.5}
			padAngle={0.7}
			cornerRadius={3}
			activeOuterRadiusOffset={8}
			borderWidth={1}
			borderColor={{ from: 'color', modifiers: [['darker', 0.2]] }}
			arcLinkLabelsSkipAngle={10}
			arcLinkLabelsTextColor="#ffffff"
			arcLinkLabelsThickness={2}
			arcLinkLabelsColor={{ from: 'color' }}
			arcLabelsSkipAngle={10}
			arcLabelsTextColor={{ from: 'color', modifiers: [['darker', 2]] }}
			legends={[
				{
					anchor: 'left',
					direction: 'column',
					justify: false,
					translateX: 0,
					translateY: 0,
					itemsSpacing: 0,
					itemWidth: 100,
					itemHeight: 18,
					itemTextColor: '#fff',
					itemDirection: 'left-to-right',
					itemOpacity: 1,
					symbolSize: 18,
					symbolShape: 'circle',
					effects: [
						{
							on: 'hover',
							style: {
								itemTextColor: '#ccc',
							},
						},
					],
				},
			]}
		/>
	);
};

export default Pie;
