import styled from 'styled-components';
import { Link } from 'react-router-dom';
import theme from '../../Theme/theme';
import bp from '../../Theme/breakpoints';
import twitter_img from '../../../assets/images/twitter.svg';
import github_img from '../../../assets/images/github.svg';
import discord_img from '../../../assets/images/discord.svg';

const StyledFooter = styled.footer`
	align-items: center;
	text-align: center;
	background: ${theme.color.background};
	width: 100%;
	padding-top: 48px;
`;

const FooterWrapper = styled.div`
	max-width: 1560px;
	margin: 0 auto;
	@media ${bp.sm} {
		display: flex;
		padding: 0 48px;
		text-align: left;
	}
`;

const FooterDiv = styled.div`
	margin-right: 0;

	@media ${bp.sm} {
		margin-right: 60px;
	}
`;

const StyledBrandLink = styled(Link)`
	display: inline-block;
	text-decoration: none;
	color: ${theme.color.text};
	font-size: 2rem;
	font-weight: bold;
	margin-bottom: 24px;

	transition: all 0.2s ease;

	&:hover {
		color: ${theme.color.accentHover};
	}
`;

const StyledList = styled.ul`
	list-style: none;
	text-align: center;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	color: ${theme.color.text};
	padding-left: 0;
	padding-bottom: 16px;

	@media ${bp.sm} {
		align-items: flex-start;
	}
`;

const StyledListItem = styled.li`
	margin-bottom: 16px;
`;

const StyledAnchor = styled.a`
	text-decoration: none;
	color: ${theme.color.text};
	transition: all 0.2s ease;

	&:hover {
		color: ${theme.color.accentHover};
	};
	}
`;

const StyledLink = styled(Link)`
	text-decoration: none;
	color: ${theme.color.text};
	margin-bottom: 16px;

	transition: all 0.2s ease;

	&:hover {
		color: ${theme.color.accentHover};
	};
	}
`;

const StyledPara = styled.p`
	margin: 16px 0;
	color: ${theme.color.background.secondary};
	font-weight: bold;

	@media ${bp.sm} {
		margin-top: 0px;
	}
`;

const LinksHeader = styled.h3`
	color: ${theme.color.background.secondary};
	margin-bottom: 24px;
	font-size: 2rem;
`;

const Footer = () => {
	return (
		<StyledFooter>
			<FooterWrapper>
				<FooterDiv></FooterDiv>
				<FooterDiv>
					<LinksHeader>Links</LinksHeader>
					<StyledList>
						<StyledLink to="/">Home</StyledLink>
					</StyledList>
				</FooterDiv>
			</FooterWrapper>
		</StyledFooter>
	);
};

export default Footer;
