import NavItemMobile from '../NavItemMobile/NavItemMobile';
import theme from '../../../Theme/theme';
import styled from 'styled-components';

const StyledNav = styled.nav`
	background: ${theme.color.background.secondary};
	position: fixed;
	top: 0;
	padding-bottom: 5px;
	width: 100vw;
	z-index: 100;
	display: canvas;
	flex-direction: column;
	align-items: center;
	padding-top: 8%;
	border-bottom-right-radius: 20px;
	border-bottom-left-radius: 20px;
	transform: translateX(200px);
	transition: all 0.3s ease-in-out;
	&.visible {
		transform: translateX(0);
		opacity: 1;
		left: 0;
	}
`;

const Divider = styled.hr`
	border-top: 1px #111111;
	width: 100%;
	opacity: 30%;
`;

const MobileMenu = ({ toggleMobileMenu }) => {
	return (
		<StyledNav>
			<NavItemMobile link="/" toggle={toggleMobileMenu} exact={true}>
				Home
			</NavItemMobile>
			<Divider />
			<NavItemMobile link="/About" toggle={toggleMobileMenu} exact={true}>
				About
			</NavItemMobile>
			<Divider />
			<NavItemMobile link="/Tokenomics" toggle={toggleMobileMenu} exact={true}>
				Tokenomics
			</NavItemMobile>
			<Divider />
			<NavItemMobile link="/Whitepaper" toggle={toggleMobileMenu} exact={true}>
				Whitepaper
			</NavItemMobile>
			<Divider />
			<NavItemMobile link="/Team" toggle={toggleMobileMenu} exact={true}>
				Team
			</NavItemMobile>
		</StyledNav>
	);
};

export default MobileMenu;
