import React, { useState, useEffect, useCallback } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import IconButton from '@material-ui/core/IconButton';
import AddIcon from '@material-ui/icons/Add';
import SendIcon from '@material-ui/icons/Send';
import ClearIcon from '@material-ui/icons/Clear';
import MoreVert from '@material-ui/icons/MoreVert';
import Share from '@material-ui/icons/Share';
import DeleteForever from '@material-ui/icons/DeleteForever';
import styled from 'styled-components';
import Picker from 'emoji-picker-react';
import bp from '../../Theme/breakpoints';
import theme from '../../Theme/theme';
import Footer from '../../Navigation/Footer/Footer';
import useUserMetadata from '../../../hooks/useUserMetadata';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import MuiDialogTitle from '@material-ui/core/DialogTitle';
import { useTheme } from '@material-ui/core/styles';
import LoadingPage from '../LoadingPage/LoadingPage';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import Avatar from '@material-ui/core/Avatar';
import PropTypes from 'prop-types';
import { keys } from '@material-ui/core/styles/createBreakpoints';
import Tooltip from '@material-ui/core/Tooltip';
import ClickAwayListener from '@material-ui/core/ClickAwayListener';
import QuizResult from '../Quiz/QuizResult';

const EmojiPicker = styled.div`
	z-index: 1;
	float: left;
	position: absolute;
`;

const EmojiPickerBackdrop = styled.div`
	position: relative;
	z-index: 2;
	float: left;
	width: 275px;
	direction: rtl;
`;

const EmojiPickerCloseButton = styled.div`
	position: absolute;
	z-index: 2;
	top: 10px;
	right: 0;
	background-color: #fff;
	margin-top: -20px;
	border-radius: 50%;
	width: 20px;
	height: 20px;
	box-shadow: 1px 2px 2px 0px #222;
`;

const QuizForm = styled.div`
	width: 90%;
	background-color: #5abe93;
	height: 600px;
	padding: 20px;
	border: solid #fff;
	border-width: 2px;
	border-radius: 15px;
	margin-bottom: 40px;
	text-align: left;
	display: block;
	margin-left: auto;
	margin-right: auto;
	@media ${bp.sm} {
		width: 40%;
		margin-left: 20px;
		margin-right: 0;
	}
`;

const MyProfile = styled.div`
	width: 90%;
	display: block;
	background-color: #b2d9b7;
	height: 600px;
	padding: 20px;
	text-align: left;
	border: solid #fff;
	border-width: 2px;
	border-radius: 15px;
	margin-bottom: 40px;
	margin-left: auto;
	margin-right: auto;
	@media ${bp.sm} {
		width: 40%;
		margin-left: 20px;
		margin-right: 0;
	}
`;

const ProfileFormLabel = styled.div`
	display: inline-block;
	text-align: left;
`;

const FormLabel = styled.div`
	display: inline-block;
	width: 140px;
	text-align: left;
`;

const QuizItemHeader = styled.div`
	vertical-align: right;
	display: flex;
	width: 75%;
`;

